import React from "react";
import { Global, css } from "@emotion/core";

export const globalStyles = (
  <Global
    styles={css`
      html {
        min-height: 100vh;
      }

      body,
      #__next {
        margin: 0;
        min-height: 100vh;
      }
    `}
  ></Global>
);
