import React, { FC } from "react";
import { Global, css } from "@emotion/core";
import styled from "@emotion/styled";

const globalStyles = (
  <Global
    styles={css`
      html {
        background: yellow;
      }
    `}
  ></Global>
);

const Title = styled.h1`
  color: red;
`;

const Home: FC = () => (
  <div>
    {globalStyles}
    <Title>Hello world!</Title>
    <img src="/platzi-logo.png" alt="logo" />
  </div>
);

export default Home;
